import React from "react";
import { useState } from "react";
import "./styles/Login.css";

export default function Login(props) {
  const [formParams, setFormParams] = useState({
    username: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormParams((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formParams.username || !formParams.password) {
      return;
    }
    // props
    //   .handleLogin({
    //     username: formParams.username,
    //     password: formParams.password,
    //   })
    //   .catch((err) => {
    //     setMessage(err.message);
    //   });
  };
  return (
    <div className="login">
      <div className="login__container">
        <h3 className="login__title">Вход</h3>
        <form
          method="get"
          name="login"
          className="login__form"
          noValidate
          onSubmit={handleSubmit}
        >
          <label className="login__label">
            <input
              className="login__item"
              name="username"
              type="text"
              id="username"
              placeholder="Email"
              required
              minLength="2"
              maxLength="40"
              value={formParams.username}
              onChange={handleChange}
            />
            <span className="popup__message first-name-error"></span>
          </label>
          <label className="popup__label">
            <input
              className="popup__item popup__item_about"
              name="password"
              type="password"
              id="password"
              placeholder="Пароль"
              required
              minLength="2"
              maxLength="200"
              value={formParams.password}
              onChange={handleChange}
            />
            <span className="popup__message prof-error"></span>
          </label>
          <button
            type="submit"
            className="popup__save-button popup__save-button_login"
          >
            Войти
          </button>
        </form>
      </div>
    </div>
  );
}
