import React from "react";
import { useState } from "react";
import PopupWithForm from "./PopupWithForm.jsx";

export default function Login(props) {
  const [formParams, setFormParams] = useState({
    username: "",
    password: "",
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormParams((prev) => ({
      ...prev,
      [name]: value,
    }));
  };
  return (
    <div className="popup popup_opened">
      <div className="popup__container">
        <h3 className={`popup__title popup__title_login`}>Вход</h3>
        <form
          method="get"
          name="login"
          className="popup__form popup__form_type_login"
          noValidate
          onSubmit={handleSubmit}
        >
          <label className="popup__label">
            <input
              className="popup__item popup__item_name"
              name="username"
              type="text"
              id="username"
              placeholder="Email"
              required
              minLength="2"
              maxLength="40"
              value={formParams.username}
              onChange={handleChange}
            />
            <span className="popup__message first-name-error"></span>
          </label>
          <label className="popup__label">
            <input
              className="popup__item popup__item_about"
              name="password"
              type="password"
              id="password"
              placeholder="Пароль"
              required
              minLength="2"
              maxLength="200"
              value={formParams.password}
              onChange={handleChange}
            />
            <span className="popup__message prof-error"></span>
          </label>
          <button
            type="submit"
            className="popup__save-button popup__save-button_login"
          >
            Войти
          </button>
        </form>
      </div>
    </div>
  );
}
